import React from 'react';

import './form-input.styles.scss';

const FormInput = ({handledChange, label, ...otherProps}) =>{
    <div className="group">
        <input className='form-input' onChange={handledChange} {...otherProps} />
        {
           label ?
            (<label className={`${otherProps.value.lenght ? 'shrink':''} form-input`}>
                {label}
            </label>) :null
        }
    </div>
}

export default FormInput;
